mapper.update:
update user_site_message
set is_read = 1
where message_id = #{messageId}
  and user_id = #{userId};

UPDATE user_site_message
SET is_read = 1, read_at = NOW()
WHERE message_id IN

    #{id}

AND user_id = #{userId}

mapper.select:
SELECT sm.id, sm.title, sm.message_type, sm.content, sm.created_time FROM site_messages sm JOIN user_site_message usm ON sm.id = usm.message_id WHERE usm.user_id = #{userId}
AND usm.is_read = #{isReadInt}
ORDER BY sm.created_time DESC
