mapper.select:
SELECT m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.query, m.route_name,
m.visible, m.status, m.is_frame, m.is_cache, m.menu_type, m.icon, m.sort,
m.permission, m.create_by, m.create_time, m.update_by, m.update_time, m.remark
FROM sys_menu m


        AND m.menu_name LIKE CONCAT('%', #{menuName}, '%')


        AND m.visible = #{visible}


        AND m.status = #{status}


        AND m.menu_type = #{menuType}


ORDER BY m.parent_id, m.sort asc
